import {AfterViewInit, Component, ElementRef, OnInit, ViewChild} from '@angular/core';
import {Route} from "@angular/router";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements AfterViewInit {

  constructor() {
  }

  @ViewChild('myDiv')
  public divElement!: ElementRef;
  public username = '<script>alert("Hello")</script>';

  public attack = '<img src=1 onerror="alert(\'YIKES!\')"';


  ngAfterViewInit(): void {
    this.divElement.nativeElement.innerHTML = this.username;

    this.el.nativeElement.innerHTML = this.attack;
    this.renderer.setProperty(this.el.nativeElement, 'innerHTML', this.attack);
  }
}

