import {AfterViewInit, Component, ElementRef, OnInit, Renderer2, ViewChild} from '@angular/core';
import {Route} from "@angular/router";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements AfterViewInit {

  constructor(private readonly renderer: Renderer2) {
  }

  @ViewChild('myDiv')
  public divElement!: ElementRef;
  public username = '<script>alert("Hello")</script>';
  @ViewChild('waybetter') public el!: ElementRef<HTMLElement>;

  public attack = '<script>alert(\'YIKES!\')</script>';


  ngAfterViewInit(): void {
    this.divElement.nativeElement.innerHTML = this.attack;
    this.renderer.setProperty(this.divElement.nativeElement, 'innerHTML', this.attack);
  }
}

<div>
  <p id="myDiv" #myDiv></p>
  <div #waybetter></div>

</div>

